import canvasState from "@/store/canvasState";
import Brush from "@/tools/Brush";

export default class ToolsSync {
  constructor(msg, socket, id) {
    this.msg = msg;
    this.socket = socket;
    this.id = id;
    this.ctx = canvasState.ctx;
    this.ctx.strokeStyle = msg.figure.color;
    this.ctx.fillStyle = msg.figure.color;
    this.ctx.lineWidth = msg.figure.width;
    this.handleDraw();
  }

  handleDraw() {
    const { figure } = this.msg;

    switch (figure.type) {
      case "brush":
        Brush.draw(this.ctx, figure.x, figure.y);
        break;

      case "rect":
        this.ctx.rect(figure.x, figure.y, figure.w, figure.h);
        this.ctx.fill();
        this.ctx.stroke();
        break;

      case "circle":
        // Добавьте логику для рисования круга
        break;

      case "eraser":
        // Добавьте логику для ластика
        break;

      case "line":
        // Добавьте логику для рисования линии
        break;

      case "up": {
        this.ctx.beginPath();
        this.sendCanvasState(this.socket, this.id);
        break;
      }

      case "canvasState": {
        const imageData = new ImageData(new Uint8ClampedArray(figure.canvasState), canvasState.canvas.width, canvasState.canvas.height);
        if (!imageData) {
          return;
        }
        // const img = new Image();
        // img.src = figure.canvasState;
        // img.onload = () => {
        //   this.ctx.clearRect(0, 0, canvasState.canvas.width, canvasState.canvas.height);
        //   this.ctx.drawImage(img, 0, 0);
        // };

        // Binary method Uint8ClampedArray
        this.ctx.putImageData(imageData, 0, 0);

        break;
      }

      default:
        console.log("Unknown figure type:", figure.type);
    }
  }

  sendCanvasState(socket, id) {
    // socket.send(
    //   JSON.stringify({
    //     id,
    //     method: "save",
    //     figure: {
    //       type: "canvasState",
    //       canvasState: canvasState.canvas.toDataURL(),
    //     },
    //   })
    // );

    // Binary method Uint8ClampedArray
    const imageData = this.ctx.getImageData(0, 0, canvasState.canvas.width, canvasState.canvas.height);
    const data = imageData.data;
    const buffer = new ArrayBuffer(data.length);
    const buf8 = new Uint8ClampedArray(buffer);
    const data8 = new Uint8ClampedArray(data);
    buf8.set(data8);
    socket.send(
      JSON.stringify({
        id,
        method: "save",
        figure: {
          type: "canvasState",
          canvasState: buf8,
        },
      })
    );

  }
}
